class Perceptron{constructor(t,i){this.weights=Array(t),this.bias=0,this.learningRate=i;for(let e=0;e<t;e++)this.weights[e]=Math.random()}predict(t){let i=0;for(let e=0;e<this.weights.length;e++)i+=t[e]*this.weights[e];return i>=0?1:0}train(t,i){let e=this.predict(t),s=i-e;for(let h=0;h<this.weights.length;h++)this.weights[h]+=this.learningRate*s*t[h];this.bias+=this.learningRate*s}}
